import React, { createContext, useContext } from "react";
import { dimensionsPropsType } from "./utils";

import "./Chart.css";

const ChartContext = createContext();
export const useChartDimensions = () => useContext(ChartContext);

const Chart = ({ dimensionsPassed, children }) => {
  console.log(dimensionsPassed);
  return (
    <ChartContext.Provider value={dimensionsPassed}>
      <svg
        className="Chart"
        width={dimensionsPassed.width}
        height={dimensionsPassed.height}
        viewBox={`0 0 ${dimensionsPassed.height} ${dimensionsPassed.width}`}
      >
        <g
          transform={`translate(${dimensionsPassed.marginLeft}, ${dimensionsPassed.marginTop})`}
        >
          {children}
        </g>
      </svg>
    </ChartContext.Provider>
  );
};

Chart.propTypes = {
  dimensions: dimensionsPropsType,
};

Chart.defaultProps = {
  dimensions: {},
};

export default Chart;
